import React, { useState } from "react";
import { ButtonGroup, Button, Slider } from "@mui/material";

//here lies jason rea, my heart and soul

const Changer = ({ handleChange }) => {
  const [isBookSelected, setBookSelected] = useState(true);

  function handleSliderChange(book) {
    setBookSelected(book);
    handleChange(null, 1);
  }

  function chooseSliderType() {
    if (isBookSelected)
      return (
        <Slider
          id='slider'
          defaultValue={1}
          step={1}
          marks
          min={1}
          max={23}
          onChange={(event, val) => handleChange(event, val)}
        />
      );
    else
      return (
        <Slider
          id='slider'
          defaultValue={1}
          step={1}
          marks
          min={1}
          max={250}
          onChange={(event, val) => handleChange(event, val)}
        />
      );
  }

  return (
    <div className='flex flex-col '>
      <ButtonGroup variant='contained'>
        <Button onClick={() => handleSliderChange(false)}>
          {" "}
          Chronological{" "}
        </Button>
        <Button onClick={() => handleSliderChange(true)}> Book </Button>
      </ButtonGroup>
      {chooseSliderType()}
    </div>
  );
};

export default Changer;
