import React, { useContext } from "react";
import { ButtonGroup, Button, Slider } from "@mui/material";
import { ValueContext } from "../page";

//here lies jason rea, my heart and soul

const Changer = ({ handleSliderChange, handleTypeChange }) => {
  const { isBookSelected } = useContext(ValueContext);
  const classesSelected =
    "w-28 outline-black-500 text-black outline bg-sky-400 rounded-md text-white";
  const classesNotSelected =
    "w-28 outline-black-500 text-black p-2  outline rounded-md mx-4 text-white";
  function chooseSliderType() {
    if (isBookSelected)
      return (
        <Slider
          id='slider'
          defaultValue={1}
          step={1}
          marks
          min={1}
          max={23}
          onChange={(event, val) => handleSliderChange(event, val)}
        />
      );
    else
      return (
        <Slider
          id='slider'
          defaultValue={1}
          step={1}
          marks
          min={1}
          max={14}
          onChange={(event, val) => handleSliderChange(event, val)}
        />
      );
  }

  return (
    <div className='flex flex-col width-100 justify-center align-center'>
      <div className='flex flex-row justify-center'>
        <button
          onClick={() => handleTypeChange(true)}
          className={isBookSelected ? classesSelected : classesNotSelected}
        >
          Book
        </button>
        <button
          onClick={() => handleTypeChange(false)}
          className={!isBookSelected ? classesSelected : classesNotSelected}
        >
          Chronological
        </button>
      </div>
      {chooseSliderType()}
    </div>
  );
};

export default Changer;
