import React, { useContext } from "react";
import { ButtonGroup, Button, Slider } from "@mui/material";
import { ValueContext } from "../page";

//here lies jason rea, my heart and soul

const Changer = ({ handleSliderChange, handleTypeChange }) => {
  const { isBookSelected } = useContext(ValueContext);
  function chooseSliderType() {
    if (isBookSelected)
      return (
        <Slider
          id='slider'
          defaultValue={1}
          step={1}
          marks
          min={1}
          max={23}
          onChange={(event, val) => handleSliderChange(event, val)}
        />
      );
    else
      return (
        <Slider
          id='slider'
          defaultValue={1}
          step={1}
          marks
          min={1}
          max={250}
          onChange={(event, val) => handleSliderChange(event, val)}
        />
      );
  }

  return (
    <div className='flex flex-col width-100 justify-center align-center'>
      <ButtonGroup variant='contained' className='flex flex-row justify-center'>
        <button className='w-24 bg-rose-600 rounded-md'>Chronological</button>
        <Button onClick={() => handleTypeChange(false)}> Chronological </Button>
        <Button onClick={() => handleTypeChange(true)}> Book </Button>
      </ButtonGroup>
      {chooseSliderType()}
    </div>
  );
};

export default Changer;
